import React from "react";


class ClassComponent extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            count: 0
        }

        
    }
   
    render() {

        const increment = () =>{
            this.setState({count: this.state.count +1})
        }


        return <div>
            <h1>{this.props}</h1>
            <h2>Count: {this.state.count}</h2>
            <button onClick={increment}>+</button>
        </div>
    }
}


export default ClassComponent
