import React from "react";



class Calculator extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            n1: null,
            n2: null,
            option: '+',
            result: 0,
            
        }
    }



    render() {




         
        return(
            <div>
                <input type='text' placeholder="Enter your number" name="n1"/>
                <select>
                    <option value="+">+</option>
                    <option value="-">-</option>
                    <option value="*">*</option>
                    <option value="+">+</option>
                    
                </select>
            </div>
        )
    }
}



export default Calculator
